import React from 'react'
import './Item.css'
import { Link } from 'react-router-dom'

const Item = (props) => {
  return (
    <div className='item'>
            <Link to={`/product/${props.id}`}><img onClick={window.scrollTo(0,100)} src={props.image} alt=''/></Link>
            <p>{props.name}</p>
            <div className='item-prices'>
                <div className='item-price-new'>
                    ${props.new_price}
                </div>
                <div className='item-price-old'>
                    ${props.old_price}
                </div>
            </div>
    </div>
  )
}

function YourComponent() {
  const handleClick = () => {
    const element = document.getElementById('scrollDestination');
    element.scrollIntoView({ behavior: 'smooth' });
  };

  return (
    <div>
      {/* Content before the scroll destination */}
      <div style={{ height: '1000px' }}>Scroll Down</div>

      {/* Element to scroll to */}
      <div id="scrollDestination">
        <img
          src="your-image.jpg"
          alt="Your Image"
          onClick={handleClick}
          style={{ cursor: 'pointer' }}
        />
      </div>

      {/* Content after the scroll destination */}
      <div style={{ height: '2000px' }}>More Content</div>
    </div>
  );
}



export default Item
